import React, {Component} from 'react';
import './PersonDetalis.css'
import {swapi} from "../../services/swapi/SwapiService";

class PersonDetalis extends Component {

    state = {
        birthYear: null,
        eyeColor: null,
        gender: null,
        id: null,
        name: null,
    }

    componentDidMount() {
        swapi.getPeople(this.props.personId)
            .then(p=>this.setState({
                ...p
            }))
    }
    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps !== this.props) {
            swapi.getPeople(this.props.personId)
                .then(p=>this.setState({
                    ...p
                }))
        }
    }

    render() {
        const {personId} = this.props
        const {birthYear, eyeColor, gender, id, name} = this.state
        return (
            <div className="person-details card">
                <img className="person-image"
                     src={`https://starwars-visualguide.com/assets/img/characters/${personId}.jpg`}
                     alt="character"/>

                <div className="card-body">
                    <h4>{name}</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Gender</span>
                            <span>{gender}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Birth Year</span>
                            <span>{birthYear}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Eye Color</span>
                            <span>{eyeColor}</span>
                        </li>
                    </ul>
                </div>
            </div>
        );
    }
}

export default PersonDetalis;
