import Spinner from "../../Spinner";

const WithData = (ViewComponent) => {
    return class extends Component {
        state = {
            data: null, // info for feature render
            loading: false,
            error: false
        }

        componentDidUpdate(prevProps) {
            if (this.props.getData !== prevProps.getData) {
                this.update()
            }
        }

        componentDidMount() {
            this.update()
        }

