/*
fetch('https://swapi.dev/api/people/1')
.then(r=>r.json())
.then(r=>console.log(r))
*/


class SwapiService {
    _startUrl = 'https://swapi.dev/api/';

    async fetchToSwapi(url) {
        const f = await fetch(this._startUrl + url.toString())
        if (!f.ok) {
            throw new Error(`Not fetch url ${url}. Error status - ${f.status}`)
        }
        return await f.json()
    }

    async getAllPeople() {
        return await this.fetchToSwapi('people').results
    }

    async getPeople(id) {
        return await this.fetchToSwapi(`people/${id}`)
    }

    async getAllPlanets() {
        return await this.fetchToSwapi('planets').results
    }

    async getPlanet(id) {
        return await this.fetchToSwapi(`planets/${id}`)
    }
}

const swapi = new SwapiService()
