import React, {Component} from 'react';
import './App.css'
import Header from "../Header";
import RandomPlanet from "../RandomPlanet";
import ErrorBoundry from "../ErrorBoundry";
import Row from "../Row";
import ItemDetalis from "../ItemDetalis";
import {swapi} from "../../services/swapi/SwapiService";


class App extends Component {

    state = {
        showRandomPlanet: false,
    }

    showPlanetHandler = () => {
        this.setState(prev => {
            return {
                showRandomPlanet: !prev.showRandomPlanet,
            }
        })
    }

    render() {
        const {showRandomPlanet} = this.state
        const personDetalis = (<ItemDetalis itemId={11} getData={swapi.getPeople} getImageUrl={null}/>)
        const starshipDetalis = (<ItemDetalis itemId={5} getData={swapi.getStarship} getImageUrl={null}/>)
        return (
            <ErrorBoundry>
                <div className={'app'}>
                    <Header/>
                    {showRandomPlanet && <RandomPlanet/>}
                    <Row left={
                        <button
                            className='btn btn-warning btn-lg toggle-planet'
                            onClick={this.showPlanetHandler}>
                            On/Off Random Planet
                        </button>
                    }/>

                    <Row left={personDetalis} right={starshipDetalis}/>
                </div>
            </ErrorBoundry>
        );
    }
}

export default App;
