class SwapiService {
    _startUrl = 'https://swapi.dev/api/';

    async fetchToSwapi(url) {
        const f = await fetch(this._startUrl + url.toString())
        if (!f.ok) {
            throw new Error(`Not fetch url ${url}. Error status - ${f.status}`)
        }
        return await f.json()
    }

    async getAllPeople() {
        const r = await this.fetchToSwapi('people')
        return r.results
    }

    async getPeople(id) {
        return await this.fetchToSwapi(`people/${id}`)
    }

    async getAllPlanets() {
        const r = await this.fetchToSwapi('planets')
        return r.results
    }

    async getPlanet(id) {
        return await this.fetchToSwapi(`planets/${id}`)
    }

    _transformPlanet(planet) {
return
        {
            id: planet.id
                name: planet.name
            population: planet.population
            rotationPeriod: planet.rotation_period
            diameter: planet.diameter
        }

    }

}

export const swapi = new SwapiService()
