import React, {Component} from 'react';
import './ItemDetalis.css'
import Spinner from "../Spinner";


const Record = (props) => {
    const {item, field, label} = props
    return (
        <li className="list-group-item">
            <span className="term">{}</span>
            <span>{birthYear}</span>
        </li>
    )
}

class ItemDetalis extends Component {

    state = {
        item: null,
        image: null,
        isLoading: true
    }

    updateItem = () => {
        const {itemId, getData, getImageUrl} = this.props
        if (!itemId) return
        getData(this.props.itemId)
            .then(p => {
                this.setState({
                    item: p,
                    image: getImageUrl(p),
                    isLoading: false
                })
            })
    }

    componentDidMount() {
        this.updateItem(this.props.itemId)
    }

    componentDidUpdate(prevProps) {
        if (this.props.itemId !== prevProps.itemId) {
            this.setState({
                person: null,
                isLoading: true
            })
            this.updateItem()
        }
    }

    render() {
        const {item, isLoading, image} = this.state
        const content = isLoading ? <Spinner/> : <PersonView {...item} imageUrl={image}/>
        return (
            <div className="person-details card">
                {content}
            </div>
        );
    }
}

export default ItemDetalis;


class PersonView extends Component {
    render() {
        const {name, gender, birthYear, eyeColor, id, imageUrl} = this.props
        return (
            <>
                <img className="person-image"
                     src={imageUrl}
                     alt="character"/>

                <div className="card-body">
                    <h4>{name || 'n/a'}</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Gender</span>
                            <span>{gender}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Birth Year</span>
                            <span>{birthYear}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Eye Color</span>
                            <span>{eyeColor}</span>
                        </li>
                    </ul>
                </div>
            </>
        );
    }
}
