import React, {Component} from 'react';
import './RandomPlanet.css'
import {swapi} from "../../services/swapi/SwapiService";
import Spinner from "../Spinner";

class RandomPlanet extends Component {
    componentDidMount() {
        this.updatePlanet()
    }

    state = {
        planet: {},
        isLoading: true
    }

    onPlanetLoaded = (planet) => {
        this.setState({planet, isLoading: false})
    }

    onError = (error) => {

    }

    updatePlanet() {
        const id = Math.floor(Math.random() * 25 + 2)
        swapi.getPlanet(id)
            .then((planet) => {
                this.onPlanetLoaded(planet)
            })
            .catch(this.onError)
    }

    render() {
        const {isLoading, planet} = this.state
        const preloader = isLoading && <Spinner/>
        const content = !isLoading && <PlanetView {...planet}/>
        return (
            <div className='random-planet jumbotron rounded'>
                {preloader}
                {content}
            </div>
        )
    }
}

export default RandomPlanet;


class PlanetView extends Component {
    render() {
        const {name, population, rotationPeriod, diameter, id} = this.props
        return (
            <>
                <img className="planet-image"
                     src={`https://starwars-visualguide.com/assets/img/planets/${id}.jpg`}
                     alt="planet"/>
                <div>
                    <h4>{name}</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Population</span>
                            <span>{population}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Rotation Period</span>
                            <span>{rotationPeriod}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Diameter</span>
                            <span>{diameter}</span>
                        </li>
                    </ul>
                </div>
            </>
        );
    }
}
