import React, {Component} from 'react';
import './ItemList.css'
import Spinner from "../Spinner";
import {swapi} from "../../services/swapi/SwapiService";

const ItemList = (props) => {
    const {data, onItemSelected, children: renderLabel} = props
    console.log(data)
    const content = data.map(p => {
        const label = renderLabel(p);
        return <li
            key={p.id}
            className='list-group-item'
            onClick={onItemSelected.bind(null, p.id)}
        >
            {label}
        </li>
    })

    return (
        <ul className='item-list list-group'>
            {content}
        </ul>
    );
}

const withData = (ViewComponent, getDataFN) => {
    return class extends Component {
        state = {
            data: null, // info for feature render
        }

        componentDidMount() {
            getDataFN()
                .then(data => {
                    this.setState({
                        data,
                        loading: false
                    })
                })
        }

        render() {
            if (!this.state.data) return <Spinner/>
            return (
                <ViewComponent data={this.state.data}/>
            )
        }
    }
}

export default withData(ItemList, swapi.getAllPeople)
