import React, {Component} from 'react';
import './ItemList.css'
import Spinner from "../Spinner";

const ItemList = (props) => {
    const {data, onItemSelected, children: renderLabel} = props
    const content = data.map(p => {
            const label = renderLabel(p);
            return <li
                key={p.id}
                className='list-group-item'
                onClick={onItemSelected.bind(null, p.id)}
            >
                {label}
            </li>
        })

    return (
        <ul className='item-list list-group'>
            {content}
        </ul>
    );
}

const withData = (ViewComponent, getDataFN) => {
    return class extends Component {
        state = {
            data: null, // info for feature render
            loading: true // show preloader
        }

        componentDidMount() {
            const {getDataFN} = this.props
            getDataFN()
                .then(data => {
                    this.setState({
                        data,
                        loading: false
                    })
                    console.log(this.state)
                })
        }

        render() {
            if (!this.state.data) return <Spinner/>
                return (
                    <ViewComponent/>
                )
        }
    }
}

export default withData(ItemList)
