import React, {Component} from 'react';
import './App.css'
import Header from "../Header";
import RandomPlanet from "../RandomPlanet";
import ErrorBoundry from "../ErrorBoundry";
import Row from "../Row";
import {PersonDetalis, PersonList, PlanetDetalis, PlanetList, StarShipDetalis, StarShipList} from "../SwComponents";


class App extends Component {

    state = {
        showRandomPlanet: false,
    }

    showPlanetHandler = () => {
        this.setState(prev => {
            return {
                showRandomPlanet: !prev.showRandomPlanet,
            }
        })
    }

    render() {
        const {showRandomPlanet} = this.state
        return (
            <ErrorBoundry>
                <div className={'app'}>
                    <Header/>
                    {showRandomPlanet && <RandomPlanet/>}
                    <Row left={
                        <button
                            className='btn btn-warning btn-lg toggle-planet'
                            onClick={this.showPlanetHandler}>
                            On/Off Random Planet
                        </button>
                    }/>

                    <PersonDetalis itemId={11}/>
                    <StarShipDetalis itemId={5}/>
                    <PlanetDetalis itemId={8}/>



                    <PersonList>
                        {({name})=><span>{name}</span>}
                    </PersonList>
                    <StarShipList>
                        {({name})=><span>{name}</span>}
                    </StarShipList>
                    <PlanetList>
                        {({name})=><span>{name}</span>}
                    </PlanetList>


                </div>
            </ErrorBoundry>
        );
    }
}

export default App;
