import React, {Component} from 'react';
import './PersonDetalis.css'
import {swapi} from "../../services/swapi/SwapiService";
import Spinner from "../Spinner";

class PersonDetalis extends Component {

    state = {
        person: null,
        isLoading: true
    }

    updatePerson = (id) => {
        swapi.getPeople(id)
            .then(this.setState({

            }))
    }

    componentDidMount() {
        this.updatePerson(this.props.personId)
    }

    /*    componentDidUpdate(prevProps, prevState, snapshot) {
            if (prevProps !== this.props) {
                swapi.getPeople(this.props.personId)
                    .then(p=>this.setState({
                        person: {...p}
                    }))
            }
        }*/

    render() {
        const {personId} = this.props
        const {person, isLoading} = this.state
        const content = isLoading ? <Spinner/> : <PersonView/>
        return (
            <div className="person-details card">
                {content}
            </div>
        );
    }
}

export default PersonDetalis;


class PersonView extends Component {
    render() {
        const {name, gender, personId, birthYear, eyeColor} = this.props
        return (
            <>
                <img className="person-image"
                     src={`https://starwars-visualguide.com/assets/img/characters/${personId}.jpg`}
                     alt="character"/>

                <div className="card-body">
                    <h4>{name}</h4>
                    <ul className="list-group list-group-flush">
                        <li className="list-group-item">
                            <span className="term">Gender</span>
                            <span>{gender}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Birth Year</span>
                            <span>{birthYear}</span>
                        </li>
                        <li className="list-group-item">
                            <span className="term">Eye Color</span>
                            <span>{eyeColor}</span>
                        </li>
                    </ul>
                </div>
            </>
        );
    }
};
